class Event 
{
	constructor(eventname)
	{
		this.eventName = eventname;
		this.callbacks = [];
	}
	
	register(callback)
	{
		this.callbacks.push(callback);
	}
	
	unregister(callback)
	{
		
	}
	
	fire(data)
	{
		/*for (var i = 0; i < this.callbacks.length; i++)
		{
			this.callbacks[i](data);
		}*/
		this.callbacks.forEach(function(callback)
		{
			callback(data);
		});
	}
}

module.exports = Event;