'use strict';

//SERVER-SIDE

class Player {
	constructor(id, name, rank, sid)
	{
		this._userid = id;
		this._name = name;
		this._rank = rank;
		this._sid = sid;
		this._isInRoom = false;
		this._inRoom = 0;
	}
	
	getID()
	{
		return this._userid;
	}
	
	getName()
	{
		return this._name;
	}
	
	setID(id)
	{
		this._id = id;
	}	
	
	getSID()
	{
		return this._sid;
	}
	
	setSID(sid)
	{
		this._sid = sid;
	}
	
	getRank()
	{
		return this._rank;
	}
	
	isInRoom()
	{
		return this._isInRoom;
	}
	
	setisInRoom(state)
	{
		this._isInRoom = state;
	}
	
	getRoom()
	{
		return this._inRoom;
	}
	
	setRoom(roomID)
	{
		this._inRoom = roomID;
	}
}

module.exports = Player;