const Player = require('player');

class PlayerHandler
{
	constructor()
	{
		this.players = [];
	}
	
	addPlayer(id, username, rank, sid)
	{
		//this.players.push(new Player(id, username, rank, sid));
		this.players[sid] = new Player(id, username, rank, sid);
		return this.players.length;
	}
	
	getPlayerIndex()
	{
		return this.players.length - 1;
	}
	
	clearPlayer(id)
	{
		this.players.splice(id, 1);
	}
	
	getPlayerCount()
	{
		return this.players.length;
	}
	
	getPlayer(index)
	{
		return this.players[index];
	}
}

module.exports = PlayerHandler;